def input_to_grid(input):
    grid = [[c for c in line] for line in input.splitlines()]
    # This is basically all we need to do, but the IDE keeps chopping off trailing whitespaces in the puzzle input. So let's make sure that all lines are the same width
    max_width = max((len(line) for line in grid))
    grid = [line + [' '] * (max_width - len(line)) for line in grid]
    return grid

def find_start_coord(grid):
    return (0,grid[0].index('|'))

def step(position, direction):
    if direction == 'd':
        return (position[0],position[1] -1)
    if direction == 'u':
        return (position[0] , position[1] +1)
    if direction == 'l':
        return (position[0] -1 , position[1])
    if direction == 'r':
        return (position[0] +1, position[1])

def reverse_direction(direction):
    return {'u':'d', 'd':'u', 'l':'r', 'r':'l'}[direction]

def change_direction(grid, position, current_direction):
    possible_next_positions = [(position[0] + offset[0], position[1] + offset[1]) for offset in [(1,0), (-1,0), (0,-1), (0,1)]]
    # Make sure we don't turn back
    previous_position = step(position, reverse_direction(current_direction))
    possible_next_positions.remove(previous_position)
    possible_next_positions = [pos for pos in possible_next_positions if pos in grid and (grid[pos] in ('|', '-', '+') or grid[pos].isalpha())]
    assert len(possible_next_positions) == 1, "If the puzzle makes sense there is always only one way to go when we change direction"

    next_position = possible_next_positions[0]
    if next_position[0] < position[0]:
        return 'l'
    elif next_position[0] > position[0]:
        return 'r'
    elif next_position[1] > position[1]:
        return 'u'
    else:
        return 'd'

    assert False, "Could not determine next step"

def walk_grid(grid, start_direction = 'd'):
    position = grid.start_coordinate()
    direction = start_direction
    letters = []
    number_of_steps = 0
    while True:
        number_of_steps += 1
        current_symbol = grid[position]
        if current_symbol.isalpha():
            assert current_symbol not in letters, "This is wrong. I encountered {} but I have been here before!".format(current_symbol)
            letters.append(current_symbol)

        if grid[position] == '+':
            direction = change_direction(grid, position, direction)
        position = step(position, direction)

        if not position in grid or grid[position] == ' ':
            break

    return (letters, number_of_steps)

def puzzle1(puzzle_input):
    g = Grid(puzzle_input)
    return ''.join(walk_grid(g)[0])

assert puzzle1(test_input) == 'ABCDEF', "Following the path to the end, the letters it sees on its path are ABCDEF."
print('The solution to puzzle one is: {}'.format(puzzle1(puzzle_input)))

def puzzle2(puzzle_input):
    g = Grid(puzzle_input)
    return walk_grid(g)[1]

assert puzzle2(test_input) == 38, "This would result in a total of 38 steps."
print('The solution to puzzle one is: {}'.format(puzzle2(puzzle_input)))
